mapper.select:
select

from user_auth
where IDENTIFIER = #{username} and CERTIFICATE=#{password}

mapper.insert:
insert into user_auth (ID, UID, IDENTITY_TYPE, 
  IDENTIFIER, CERTIFICATE, CREATE_TIME, 
  UPDATE_TIME, STATUS, USER_TYPE
  )
values (#{id,jdbcType=INTEGER}, #{uid,jdbcType=INTEGER}, #{identityType,jdbcType=TINYINT}, 
  #{identifier,jdbcType=VARCHAR}, #{certificate,jdbcType=VARCHAR}, #{createTime,jdbcType=INTEGER}, 
  #{updateTime,jdbcType=INTEGER}, #{status,jdbcType=TINYINT}, #{userType,jdbcType=TINYINT}
  )
